mapper.select:
select uid, pw, name, email, age
from user
where uid = #{uid} and pw = #{pw}

select uid, pw, name, email, age
from user
where uid = #{uid} and pw = #{pw}

mapper.update:
update user
name = #{name}, email = #{email}, age = #{age},
where uid = #{uid}

mapper.select:
select uid, pw, name, email, age
from user
where uid = #{uid} and name = #{name} and email = #{email} and age = #{age}
